                    // CPF (apenas leitura - não pode ser editado)
                    // Nota: O modelo Crianca não tem campo CPF definido, mas podemos adicionar se necessário
                    // Por enquanto, vou comentar esta seção
                    /*
                    if (!isEditMode && !child.cpf.isNullOrEmpty()) {
                        item {
                            Column {
                                Text(
                                    text = "CPF",
                                    fontSize = 12.sp,
                                    color = Color.Gray,
                                    fontWeight = FontWeight.SemiBold
                                )
                                Text(
                                    text = formatarCPF(child.cpf),
                                    fontSize = 16.sp,
                                    fontWeight = FontWeight.Bold,
                                    color = Color.Black
                                )
                            }
                        }
                    }
                    */

                    // Cadastrado em (apenas leitura)
                    if (!isEditMode) {
                        item {
                            Column {
                                Text(
                                    text = "Cadastrado em",
                                    fontSize = 12.sp,
                                    color = Color.Gray,
                                    fontWeight = FontWeight.SemiBold
                                )
                                Text(
                                    text = formatarData(child.criado_em),
                                    fontSize = 16.sp,
                                    fontWeight = FontWeight.Bold,
                                    color = Color.Black
                                )
                            }
