                        if (onStartConversation != null) {
                            Button(
                                onClick = { onStartConversation() },
                                modifier = Modifier
                                    .fillMaxWidth()
                                    .height(48.dp),
                                colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF1976D2))
                            ) {
                                Text("Iniciar Conversa", color = Color.White, fontWeight = FontWeight.Bold)
                            }
                        }

